package com.example.myapplication

import android.annotation.SuppressLint
import android.app.Application
import android.os.Bundle
import android.widget.TextView
import androidx.activity.result.ActivityResultCallback
import androidx.activity.result.ActivityResultLauncher
import androidx.activity.result.contract.ActivityResultContract
import androidx.appcompat.app.AppCompatActivity


class ScrollingText : AppCompatActivity() {
    @SuppressLint("ResourceType")
    val txt1 = ""
    val txt2 = ""
    val txt3 = ""

    override fun registerActivityLifecycleCallbacks(callback: Application.ActivityLifecycleCallbacks) {
        super.registerActivityLifecycleCallbacks(callback)
    }

    override fun onCreate(savedInstanceState: Bundle?) {
        super.onCreate(savedInstanceState)
        setContentView(R.layout.activity_scrolling_text)
        when (intent.getStringExtra("message")) {
            "text1" -> findViewById<TextView>(R.id.article).text = txt1
            "text2" -> findViewById<TextView>(R.id.article).text = txt2
            "text3" -> findViewById<TextView>(R.id.article).text = txt3
        }
    }
}